: none !important;min-width: 0 !important;width: 0 !important;min-height: 0 !important;height: 0 !important;max-height: 0!important;}}@media only screen and (max-width:374px) {body:not([yahoo]) .footer-menu-link {font-size: 11px!important;line-height: 12px!important;}}img {outline: none;text-decoration: none;-ms-interpolation-mode: bicubic;}a img {border: none;}.image_fix {display: block;}p {margin: 1em 0;}h1,h2,h3,h4,h5,h6 {color: #333333 !important;}h1 a,h2 a,h3 a,h4 a,h5 a,h6 a {color: #0099ff !important;}h1 a:hover,h2 a:hover,h3 a:hover,h4 a:hover,h5 a:hover,h6 a:hover {text-decoration: underline;}h1 a:active,h2 a:active,h3 a:active,h4 a:active,h5 a:active,h6 a:active {color: #0099ff !important;}h1 a:visited,h2 a:visited,h3 a:visited,h4 a:visited,h5 a:visited,h6 a:visited {color: #0099ff !important;}table td {border-collapse: collapse;}table {border-collapse: collapse;mso-table-lspace: 0pt;mso-table-rspace: 0pt;}a {color: #0099ff;text-decoration: none;}a:hover {text-decoration: none!important;}.button-label:hover {color: #ffffff !important;text-decoration: none !important;}.mobile-visible {}.mobile-hidden {}</style><!--[if gte mso 9]><xml><o:OfficeDocumentSettings><o:AllowPNG/><o:PixelsPerInch>96</o:PixelsPerInch></o:OfficeDocumentSettings></xml><![endif]--></head><body style="color:#333333;font-family:Arial, sans-serif;-webkit-text-size-adjust:100%;" leftmargin="0" topmargin="0" marginwidth="0" marginheight="0"><!--WRAPPER--><table style="background:#fff;" width="100%" bgcolor="#ffffff" border="0" cellpadding="0" cellspacing="0" align="center"><tr><td style="background:#f5f5f5;padding:0 4%;vertical-align:top;" class="l-wrapper" valign="top"><!--HEADER--><div style="width:680px;margin:0 auto;" class="device-width"><!--[if mso]><table width="680" style="width:680px;" border="0" cellpadding="0" cellspacing="0" align="center"><![endif]--><!--[if !mso]><!--><table width="100%" style="width:100%;" border="0" cellpadding="0" cellspacing="0" align="center"><!--<![endif]--><tr><td style="padding-top:20px;padding-bottom:20px;vertical-align:middle;" valign="middle" align="left"><table class="logo" style="vertical-align:middle;" border="0" cellpadding="0" cellspacing="0" align="left"><tr><td class="logo" style="padding:0 53px 0 20px;vertical-align:middle;" valign="middle" align="center"><a style="text-decoration:none;" href="https://hh.ru/" target="_blank"><!--[if !mso]><!--><img class="mobile-visible" style="vertical-align:top;display:none;min-width:0;width:0;min-height:0;height:0;max-height:0;overflow:hidden;" border="0" src="https://hhcdn.ru/icms/10044674.svgz" alt="" /><!--<![endif]--><img class="mobile-hidden" style="vertical-align:top;" border="0" src="https://hhcdn.ru/icms/10044690.png " alt="" /></a></td></tr></table><table class="navi" style="vertical-align:middle;" border="0" cellpadding="0" cellspacing="0" align="left"><tr><td style="padding-top:15px;padding-bottom:15px;vertical-align:top;" valign="top" align="left"><table style="margin:3px 0" border="0" cellpadding="0" cellspacing="0" align="left"><tr><td style="padding-left:17px;padding-right:17px;border-right:1px solid #c4c4c4;vertical-align:middle;font:14px/14px Arial, sans-serif;" valign="middle" align="left"><a style="color:#333333;text-decoration:none;" href="https://inside.hh.ru/news/Pages/default.aspx" target="_blank">Новости</a></td><!--[if !mso]><!--></tr></table><table style="margin:3px 0" border="0" cellpadding="0" cellspacing="0" align="left"><tr><!--<![endif]--><td style="padding-left:17px;padding-right:17px;border-right:1px solid #c4c4c4;vertical-align:middle;font:14px/14px Arial, sans-serif;" valign="middle" align="left"><a style="color:#333333;text-decoration:none;" href="https://inside.hh.ru/communication/ads/Pages/default.aspx" target="_blank">Объявления</a></td><!--[if !mso]><!--></tr></table><table style="margin:3px 0" border="0" cellpadding="0" cellspacing="0" align="left"><tr><!--<![endif]--><td style="padding-left:17px;vertical-align:middle;font:14px/14px Arial, sans-serif;" valign="middle" align="left"><a style="color:#333333;text-decoration:none;" href="https://inside.hh.ru/communication/thankee/Pages/default.aspx" target="_blank">Спасибо!</a></td><!--[if !mso]><!--></tr></table><table style="margin:3px 0" border="0" cellpadding="0" cellspacing="0" align="left"><tr><!--<![endif]--></td></tr></table></td></tr></table></td></tr></table></div><!--/header--><!--BODY--><div style="width:680px;margin:0 auto;" class="device-width"><!--[if mso]><table width="680" style="width:680px;" border="0" cellpadding="0" cellspacing="0" align="center"><![endif]--><!--[if !mso]><!--><table width="100%" style="width:100%;" border="0" cellpadding="0" cellspacing="0" align="center"><!--<![endif]--><tr><td style="padding:0;vertical-align:top;" valign="top"><!--Шаблон самого содержимого письма (выделяется в отдельный шаблон)--><table style="width:100%;" width="100%" border="0" cellpadding="0" cellspacing="0" align="left"><tr><td style="padding:0;vertical-align:top;text-align:left;color:#333333;font-family:Arial, sans-serif;font-size:14px;font-weight:normal;line-height:20px;" class="l-content" valign="top"><!--CONTENT--><!-- Базовый блок --><!-- начало блока новости --><table style="width:100%;" width="100%" border="0" cellpadding="0" cellspacing="0"><tr><td class="adaptive-no-vert-shift" style="padding:0;vertical-align:top;text-align:left;" valign="top"><table style="width:100%;" width="100%" border="0" cellpadding="0" cellspacing="0"><tr><a style="text-decoration:none;" href="https://inside.hh.ru/Pages/default.aspx"><img class="adaptive-col" style="vertical-align:top;width:100%;" width="100%" border="0" src="https://hhcdn.ru/file/16502651.png" alt="" /></a></td></tr><tr><td class="adaptive-padding" style="background:#fff;padding:12px 30px 35px;vertical-align:top;text-align:left;" valign="top"></br><p style="margin:5px 0 0;color:#777777;font:16px/22px Arial,sans-serif;"><b><font color="#FF0000">{{userName}}, приветствую тебя!</font></b></p><p style="margin:5px 0 0;color:#777777;font:16px/22px Arial,sans-serif;"></br>С первым рабочим днем тебя в самой замечательной компании!</br></br>Ты {{isMale}} много времени, общаясь с нашими коллегами из HR и со своими будущими коллегами для того, чтобы мы окончательно убедились, что именно ты лучшим образом сможешь применить свои знания, опыт и навыки. Принести максимальную пользу нашей компании: научить своих коллег тому уникальному, что ты знаешь и умеешь, а заодно и самой получить новые знания от коллег и вывести свою карьеру на новый уровень.</br></br>Компания заинтересована в том, чтобы ты как можно быстрее освоилась на новом месте и смогла в полной мере проявить все свои качества. Для этого, в первое время работы ты сможешь изучить специальные материалы. Оказывать поддержку тебе будут опытные наставники – замечательные сотрудники НН.</br>И чтобы тебе было удобно ориентироваться в компании и осваивать «жизнь в НН» – мы подготовили путеводитель, который будет твоим помощником и навигатором. В путеводителе содержится важная и полезная информация о компании, ее структуре, команде, внутренних особенностях, правилах взаимодействие с коллегами и многом другом.</br>Мы очень хотим, чтобы каждый твой новый трудовой день проходил насыщенно и интересно, продуктивно и красочно! </br></br>И добро пожаловать на борт HeadHunter!</br>Попутного ветра, отличных приключений и достижения целей! </br></br>PS: Будут вопросы – заходи: моя дверь всегда открыта.</td></tr></table></td></tr></table><!-- конец блока новости --><!-- начало блока именинников --><table style="width:100%;" width="100%" border="0" cellpadding="0" cellspacing="0"><tr><td class="adaptive-no-vert-shift" style="padding:0;vertical-align:top;text-align:left;" valign="top"><table style="width:100%;" width="100%" border="0" cellpadding="0" cellspacing="0"><tr></td></tr><tr><td class="adaptive-padding" style="background:#fff;padding:12px 30px 35px;vertical-align:top;text-align:left;" valign="top"><!-- именинник сам начало --><div><table><caption></caption><tbody><tr><td> </br></br> <p style="margin:5px 0 0;color:#777777;font:16px/22px Arial,sans-serif;"><b><font color="#FF0000"><a href="https://wiki.hh.ru/pages/viewpage.action?pageId=143786022"> Мы подготовили для тебя специальный гид. Кликай и узнавай новое и важное</a></font></b></p><p style="margin:5px 0 0;color:#777777;font:16px/22px Arial,sans-serif;"></br><b><font color="#FF0000"><a href="https://wiki.hh.ru/pages/viewpage.action?pageId=143786027"> А это люди, которых тебе обязательно нужно знать!</a></font></b></p></td></tr></tbody></table><!-- конец именинника --> <!-- именинник сам начало --><div><table><caption></caption><tbody><tr><td></br></br></br></br><img class="adaptive-col" style="vertical-align:top;" border="0" src="https://hhcdn.ru/file/16502673.png" alt="" /></td><td><br/><br/><p style="margin:5px 0 0;color:#777777;font:16px/22px Arial,sans-serif;">Верь в себя</p><p style="margin:5px 0 0;color:#777777;font:16px/22px Arial,sans-serif;">Создавай hh.ru</p><p style="margin:5px 0 0;color:#777777;font:16px/22px Arial,sans-serif;">Успех неизбежен©</p></td></tr></tbody></table><!-- конец именинника --><!-- вставлять нового именинника сюда, добавить выше <br><br> при необходимости выше --></div></td></tr></table></td></tr></table><!-- конец блока именинников --><!--FOOTER--><div style="width:680px;margin:0 auto;" class="device-width"><!--[if mso]><table width="680" style="width:680px;background:#f5f5f5;" border="0" cellpadding="0" cellspacing="0" align="center"><![endif]--><!--[if !mso]><!--><table width="100%" style="width:100%;background:#f5f5f5;" border="0" cellpadding="0" cellspacing="0" align="center"><!--<![endif]--><tr><td style="padding:0;vertical-align:top;text-align:left;" valign="top"><!--[if !mso]><!--><table class="device-clear" style="min-width:64%;" border="0" cellpadding="0" cellspacing="0" align="left"><!--<![endif]--><!--[if mso]><table class="device-clear" border="0" cellpadding="0" cellspacing="0" align="left"><![endif]--><tr><td class="adaptive-padding" style="padding:10px 0 0 30px;vertical-align:top;text-align:left;" valign="top"><table class="footer-menu" border="0" cellpadding="0" cellspacing="0"><tr><td style="padding:0;vertical-align:top;text-align:left;" valign="top"><div style="color:#777777;font:12px/20px Arial, sans-serif;" class="footer-copyright-text">Группа компаний HeadHunter</div></td></tr></table></td></tr></table><!--[if !mso]><!--><table class="device-clear" border="0" cellpadding="0" cellspacing="0" align="left"><!--<![endif]--><!--[if mso]><table class="device-clear" border="0" cellpadding="0" cellspacing="0" align="right"><![endif]--><tr><td class="adaptive-padding socials" align="left" valign="middle" style="text-align:right;padding:10px 30px 0;"><table border="0" cellpadding="0" cellspacing="0" align="center"><tr><td style="padding:0 25px 0 0;vertical-align:middle;text-align:left;" valign="middle"><a href="https://vk.com/headhunter" target="_blank"><!--[if !mso]><!--><img class="mobile-visible" src="https://hhcdn.ru/icms/10044679.svgz" border="0" style="vertical-align:middle;border:none !important;display:none;min-width:0;width:0;min-height:0;height:0;max-height:0;overflow:hidden;" alt=""><!--<![endif]--><img class="mobile-hidden" src="https://hhcdn.ru/icms/10044696.png" border="0" style="vertical-align:middle;border:none !important;" alt=""></a></td><td style="padding:0 25px 0 0;vertical-align:middle;text-align:left;" valign="middle"><a href="https://www.facebook.com/headhuntergroup" target="_blank"><!--[if !mso]><!--><img class="mobile-visible" src="https://hhcdn.ru/icms/10044675.svgz" border="0" style="vertical-align:middle;border:none !important;display:none;min-width:0;width:0;min-height:0;height:0;max-height:0;overflow:hidden;" alt=""><!--<![endif]--><img class="mobile-hidden" src="https://hhcdn.ru/icms/10044692.png" border="0" style="vertical-align:middle;border:none !important;" alt=""></a></td></tr></table></td></tr></table></td></tr></table></div><!--/footer--></td></tr><!--Растяжка для outlook--><tr><td style="padding:0 300px 0 0"></td></tr><!--/Растяжка для outlook--></table><!--/wrapper--></body></html>
');

INSERT INTO mail_template VALUES (default, 'chief_missions', 'Привет. Скоро к твоей команде присоеденится новый сотрудник {{userName}}. Пожалуйста, приготовь список задач на испытательный срок, пройдя по ссылке {{url}}');
INSERT INTO mail_template VALUES (default, 'hr_missions', 'Привет. Скоро к нашей компании присоеденится новый сотрудник {{userName}}. Будущий руководитель подготовил задачи на испытательный период {{missions}}');
INSERT INTO mail_template VALUES (default, 'meeting_room', 'Привет. {{userName}} ожидает {{meetingType}}. Не забудь забронировать переговрную комнату для этого события.');
INSERT INTO mail_template VALUES (default, 'meeting_results', 'Привет. Для сотрудника {{userName}} была проведена {{meetingType}} встреча. Пожалуйста, заполни итоги встречи, перейдя по ссылке {{url}}');
INSERT INTO mail_template VALUES (default, 'questionnaire', 'Привет. Твой испытательный период практически завершен. Пожалуйста, заполни опросник новчика, перейдя по ссылке {{url}}');
